import { ref } from "vue";
export default {
  name: "AddTaskForm",
  setup() {
    const newTaskName = ref("");
    const onSubmit = () => {
      if (newTaskName.value.length !== 0) {
        this.$emit("submit", newTaskName.value);
        this.newTaskName = "";
      }
    };
    const closeForm = () => {
      emit("cancel");
    };
    return {
      newTaskName,
      onSubmit,
      closeForm
    };
  }
};
