import { ref } from "vue";
export default {
  name: "AddTaskForm",
  setup(props, {
    emit
  }) {
    const newTaskName = ref("");
    const onSubmit = () => {
      if (newTaskName.value.length !== 0) {
        emit("submit", newTaskName.value);
        newTaskName.value = "";
      }
    };
    const closeForm = () => {
      emit("cancel");
    };
    return {
      newTaskName,
      onSubmit,
      closeForm
    };
  }
};
